import { auth, provider } from "../Firebase";
import { SET_USER } from "./ActionType";

export const setUser = (payload) => ({
  type: SET_USER,
  user: payload,
});

export function signInAPI() {
  auth
    .signInWithPopup(provider)
    .then((payload) => {
      dispatch(payload.user);
    })
    .catch((error) => {
      console.log(error);
    });
}
