import styled from 'styled-components';

const Login = (props) =>{
  return (
    <Container>
      <Nav>
        <a href="/">
          <img src="./images/login-logo.svg" />
        </a>
        <div>
          <Join>Join Now</Join>
          <SignIn>Sign In</SignIn>
        </div>
      </Nav>
      <Section>
        <Hero>
            <h1>Welcome to your professional Community</h1>
            <img src="/images/login-hero.svg" alt="img" />
        </Hero>
        <Form>
          <Google>
            <img src="/images/google.svg" />
            Sign In With Google
          </Google>
        </Form>
      </Section>
    </Container>
  )
}
const Container = styled.div``;
const Nav = styled.nav`
  max-width: 1128px;
  margin: auto;
  padding: 12px 12px 0 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  flex-wrap: nowrap;
  & > a {
    width: 135px;
    height: 24px;
    @media(max-width: 768px){
      padding: 0 5px;
    }
  }
`;
const Join = styled.a`
  font-size: 16px;
  font-weight: 400;
  padding: 10px 12px;
  margin-right: 20px;
  color: rgba(0,0,0,0.6);
  border-radius: 4px;
  text-decoration: none;
  &:hover{
    background-color: rgba(0,0,0,0.08);
    color: rgba(0,0,0,0.9);
    cursor: pointer
  }
`;
const SignIn = styled.a`
  box-shadow: inset 0 0 0 1px #0a66c2;
  padding: 10px 12px;
  border-radius: 24px;
  color: #0a66c2;
  line-height: 40px;
  font-weight: 800;
  font-style: 16px;
  transition: 127ms;
  &:hover{
    background-color: rgba(112, 181, 249, 0.15);
      color: #0a66c2;
      text-decoration: none;
      cursor: pointer;
  }

`;
const Section = styled.div`
display: flex;
align-items: start;
min-height: 700px;
padding-bottom: 138px;
padding-top: 40px;
padding: 40px 0 13px 60px;
position: relative;
flex-wrap: wrap;
width: 100%;
max-width: 1128px;
align-items: center;
margin: auto;
@media(max-width: 768px){
  margin: auto;
  min-height: 0;
}
`
const Hero = styled.div`
    width: 100%;
    h1{
      padding-bottom: 0;
      width: 55%;
      font-size: 56px;
      color: #2977c9;
      font-weight: 200;
      line-height: 70px;
      @media(max-width: 768px){
        text-align: center;
        font-size: 20px;
        width: 100%;
        line-height: 2;
      }
    }
    img{
      /* z-index: -1; */
      width: 700px;
      height: 670px;
      position: absolute;
      bottom: -2px;
      right: -150px;
      @media(max-width: 768px){
        top: 230px;
        width:initial;
        position: initial;
        height: initial;
      }

    }
  `;
  const Form = styled.div`
    margin-top: 100px;
    width: 408px;
    @media(max-width:768px){
      margin-top: 20px;
    }
  `;
  const Google = styled.button`
    display: flex;
    align-items: center;
  `;
export default Login;
