import React from "react";
import styled from "styled-components";

function PostModal(props) {
  return (
    <div>
      <Container>
        <Content>
          <Header>
            <h2>Create a post</h2>
            <button>
              <p>X</p>
            </button>
          </Header>
          <ShareContent>
            <UserInfo>
              <img src="/images/user.svg" alt="" />
              <span>Name</span>
            </UserInfo>
          </ShareContent>
        </Content>
      </Container>
    </div>
  );
}
const Container = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 9999;
  color: black;
  background-color: rgba(0, 0, 0, 0.8);
`;
const Content = styled.div`
  width: 100%;
  max-width: 552px;
  background-color: #fff;
  max-height: 90%;
  overflow: initial;
  border-radius: 5px;
  position: relative;
  display: flex;
  flex-direction: column;
  top: 32px;
  margin: 0 auto;
`;
const Header = styled.div`
  display: block;
  padding: 16px 20px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.15);
  font-size: 16px;
  line-height: 1.5;
  color: rgba(0, 0, 0, 0.6);
  font-weight: 400;
  display: flex;
  align-items: center;
  justify-content: space-between;
  button {
    height: 40px;
    width: 40px;
    min-width: auto;
    background-color: rgba(0, 0, 0, 0.15);
    border: none;
    border-radius: 5px;
    color: rgba(0, 0, 0, 0.5);
    &:hover {
      cursor: pointer;
    }
  }
`;
const ShareContent = styled.div`
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  overflow-y: auto;
  vertical-align: baseline;
  background: transparent;
  padding: 8px 12px;
`;
const UserInfo = styled.div`
  display: flex;
  align-items: center;
`;
export default PostModal;
