import React from "react";
import styled from "styled-components";
import { useState } from "react";

function PostModal(props) {
  const [editorText, setEditorText] = useState("");
  return (
    <div>
      <Container>
        <Content>
          <Header>
            <h2>Create a post</h2>
            <button>X</button>
          </Header>
          <ShareContent>
            <UserInfo>
              <img src="/images/user.svg" alt="" />
              <span>Name</span>
            </UserInfo>
            <Editor>
              <textarea
                value={editorText}
                onChange={(e) => setEditorText(e.target.value)}
                placeholder="What do you want to talk about?"
                autofocus={true}
              ></textarea>
            </Editor>
          </ShareContent>
          <SharedCreation>
            <PostButton>Post</PostButton>
          </SharedCreation>
        </Content>
      </Container>
    </div>
  );
}
const Container = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 9999;
  color: black;
  background-color: rgba(0, 0, 0, 0.8);
`;
const Content = styled.div`
  width: 100%;
  max-width: 552px;
  background-color: #fff;
  max-height: 90%;
  overflow: initial;
  border-radius: 5px;
  position: relative;
  display: flex;
  flex-direction: column;
  top: 32px;
  margin: 0 auto;
`;
const Header = styled.div`
  display: block;
  padding: 16px 20px;
  border-bottom: 1px solid rgba(0, 0, 0, 0.15);
  font-size: 16px;
  line-height: 1.5;
  color: rgba(0, 0, 0, 0.6);
  font-weight: 400;
  display: flex;
  align-items: center;
  justify-content: space-between;
  button {
    height: 40px;
    width: 40px;
    min-width: auto;
    background-color: rgba(0, 0, 0, 0.15);
    border: 1px solid rgba(0, 0, 0, 0.15);
    border-radius: 5px;
    color: rgba(0, 0, 0, 0.5);
    &:hover {
      cursor: pointer;
    }
  }
`;
const ShareContent = styled.div`
  display: flex;
  flex-direction: column;
  flex-grow: 1;
  overflow-y: auto;
  vertical-align: baseline;
  background: transparent;
  padding: 8px 12px;
`;
const UserInfo = styled.div`
  display: flex;
  align-items: center;
  padding: 12px 24px;
  svg,
  img {
    width: 48px;
    height: 48px;
    border: 2px solid transparent;
    border-radius: 50%;
  }
  span {
    font-size: 16px;
    font-weight: 600;
    margin-left: 4px;
    line-height: 1.5;
  }
`;
const SharedCreation = styled.div`
  display: flex;
  justify-content: flex-end;
  padding: 12px 24px 12px 16px;
`;
const PostButton = styled.button`
  padding: 8px 10px;
  border-radius: 5px;
  border: none;
  min-width: 60px;
  background: #0a66c2;
  color: #fff;
  &:hover {
    background-color: #004182;
  }
`;
const Editor = styled.div`
  padding: 12px 24px;
  textarea {
    width: 100%;
    min-height: 100px;
    resize: none;
    font-size: 14px;
    padding: 4px;
    outline: none;
    border: 1px solid rgba(0, 0, 0, 0.5);
    border-radius: 5px;
  }
  input {
    width: 100%;
    height: 35px;
    font-size: 12px;
  }
`;
export default PostModal;
