import styled from 'styled-components';

const Login = (props) =>{
  return (
    <Container>
      <Nav>
        <a href="/">
          <img src="./images/login-logo.svg" />
        </a>
        <div>
          <Join>Join Now</Join>
          <SignIn>Sign In</SignIn>
        </div>
      </Nav>
      <Section>
        <Hero>
            <h1>Welcome to your professional Community</h1>
        </Hero>
      </Section>
    </Container>
  )
}
const Container = styled.div``;
const Nav = styled.nav`
  max-width: 1128px;
  margin: auto;
  padding: 12px 12px 0 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  position: relative;
  flex-wrap: nowrap;
  & > a {
    width: 135px;
    height: 24px;
    @media(max-width: 768px){
      padding: 0 5px;
    }
  }
`;
const Join = styled.a`
  font-size: 16px;
  font-weight: 400;
  padding: 10px 12px;
  margin-right: 20px;
  color: rgba(0,0,0,0.6);
  border-radius: 4px;
  text-decoration: none;
  &:hover{
    background-color: rgba(0,0,0,0.08);
    color: rgba(0,0,0,0.9);
    cursor: pointer
  }
`;
const SignIn = styled.a`
  box-shadow: inset 0 0 0 1px #0a66c2;
  padding: 10px 12px;
  border-radius: 24px;
  color: #0a66c2;
  line-height: 40px;
  font-weight: 800;
  font-style: 16px;
  transition: 127ms;
  &:hover{
    background-color: rgba(112, 181, 249, 0.15);
      color: #0a66c2;
      text-decoration: none;
      cursor: pointer;
  }

`;
const Section = styled.div`
display: flex;
align-items: start;
min-height: 700px;
padding-bottom: 138px;
padding-top: 40px;
padding: 40px 0 13px 60px;
position: relative;
flex-wrap: wrap;
width: 100%;
max-width: 1128px;
align-items: center;
margin: auto;
@media(max-width: 768px){
  margin: auto;
  min-height: 0;
}
`
const Hero = styled.div``;
export default Login;
