import { SET_LOADING_STATUS } from "../action/ActionType";
export const initialState = {
  loading: false,
};

const articleReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_LOADING_STATUS: {
      return {
        ...state,
        loading: action.loading,
      };
      break;
    }
    default: {
      return state;
    }
  }
};

export default articleReducer;
